mapper.select:
SELECT calendar.*, date_format(contract.contract_sdate, '%Y-%m-%d %H:%i') start, date_add(date_format(contract.contract_edate, '%Y-%m-%d %H:%i'), INTERVAL 1 DAY) end, keyword.key_name title, user.user_name userName
FROM calendar JOIN contract ON calendar.contract_code=contract.contract_code JOIN keyword ON keyword.key_code=contract.key_code JOIN user ON contract.user_code=user.user_code
WHERE calendar.co_code=#{coCode}
